let inlist = List.rev (String.fold input ~init:[] ~f:(fun acc a -> a :: acc));;

let rec floor l =
  match l with
  | [] -> 0
  | ')' :: tl -> -1 + floor tl
  | '(' :: tl -> 1 + floor tl
;;

floor inlist;;

(* start second part *)

let firstnegative il =
  let rec first_sub l pos sum =
    match l with
    | '(' :: tl -> first_sub tl (pos+1) (sum+1)
    | ')' :: tl ->
        if sum > 0 then first_sub tl (pos+1) (sum-1)
        else (pos+1)
  in first_sub il 0 0
;;

firstnegative inlist;;
